    void guardarOperador1() {
        float numero; //inicia la var. numero
        numero = Float.valueOf(espacio.getText()); //guarda en numero el valor de espacio

        miCalculador.operador1 = numero; //objeto.atributo=valor

        espacio.setText("");//espacio queda en blanco
    }
    
    private void btnSumaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnSumaMouseClicked
        // TODO add your handling code here:
        guardarOperador1();
        operador = 0;
    }//GEN-LAST:event_btnSumaMouseClicked

    private void btnRestaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnRestaMouseClicked
        // TODO add your handling code here:
        guardarOperador1();
        operador = 1;
    }//GEN-LAST:event_btnRestaMouseClicked

    private void btnIgualMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnIgualMouseClicked
        // TODO add your handling code here:
        float numero;
        numero = Float.valueOf(espacio.getText());

        miCalculador.operador2 = numero;

        switch (operador) {//condici√≥n de operador para evitar escribir tanto
            case 0:
                miCalculador.sumar();
            case 1:
                miCalculador.restar();
        }
    }//GEN-LAST:event_btnIgualMouseClicked

